import React, {Component} from 'react';
import axios from "axios";

class PostForm extends Component {
    constructor(props) {
        super(props);

        this.state = {
            attributes:'',
            schema_name: '',
            schema_version: ''
        }
    }

    changeHandler = (e) => {
        this.setState({[e.target.name]: e.target.value})
    }

    submitHandler = e => {
        e.preventDefault()
        console.log(this.state)
        axios.post
    }

    render() {
        const {attributes, schema_name, schema_version} = this.state
        return (
            <div>
                <form onSubmit={this.submitHandler}>
                    <div>
                        <input type="text"
                               name="attributes"
                               value={attributes}
                               onChange={this.changeHandler}
                        />
                    </div>
                    <div>
                        <input type="text"
                               name="schema_name"
                               value={schema_name}
                               onChange={this.changeHandler}
                        />
                    </div>
                    <div>
                        <input type="text"
                               name="schema_version"
                               value={schema_version}
                               onChange={this.changeHandler}
                        />
                    </div>
                    <button type="submit">Submit</button>
                </form>
            </div>
        );
    }
}


export default PostForm;
