import React, {Component} from 'react';
import axios from "axios";


class PostForm extends Component {
    constructor(props) {
        super(props);

        this.state = {
            attributes: '',
            schema_name: '',
            schema_version: ''
        }
    }

    changeHandler = (e) => {
        this.setState({[e.target.name]: e.target.value})
    }

    submitHandler = e => {
        e.preventDefault()
        console.log(this.state)
        axios.post('http://localhost:7080/schemas',this.state[0].toArray()).then(response => {console.log(response)}).catch(error => {console.log(error)})
    }

    render() {
        const {attributes, schema_name, schema_version} = this.state
        return (
            <div>
                <form onSubmit={this.submitHandler}>
                    Attributes:
                    <div>
                        <input type="text"
                               name="attributes"
                               value={attributes}
                               onChange={this.changeHandler}
                        />
                    </div>
                    Schema-Name:
                    <div>
                        <input type="text"
                               name="schema_name"
                               value={schema_name}
                               onChange={this.changeHandler}
                        />
                    </div>
                    Schema-Version:
                    <div>

                        <input type="text"
                               name="schema_version"
                               value={schema_version}
                               onChange={this.changeHandler}
                        />
                    </div>
                    <button type="submit">Submit</button>
                </form>
            </div>
        );
    }
}


export default PostForm;
