import React, { Component } from 'react'
import './App.css'
import axios from 'axios'
//import PostForm from "./PostForm";
var QRCode = require('qrcode.react');

class App extends Component {
    constructor (probs) {
        super(probs)
        this.state = {
            invitation_url: '',
            schema:''
        }

        this.handleClick = this.handleClick.bind(this)
    }

    handleClick () {
        console.log('Success!')
        axios.post('http://localhost:7080/connections/create-invitation').then(response => this.setState({invitation_url: response.data.invitation_url}))

    }

    handleClick2 () {
      console.log('Success!')
      axios.post('http://localhost:7080/connections/create-invitation').then(response => this.setState({invitation_url: response.data.invitation_url}))

  }

  render () {
    return (
            <div>
                <div className='button__container'>
                    <h2> Hamburg nach Köln:</h2>
                      <button className='button' onClick={this.handleClick}>Jetzt Zug buchen!</button>
                      <p>{this.state.invitation_url}</p>
                      <QRCode value={this.state.invitation_url}/>
                </div>
                <div classname='button2__container'>
                    <h2> Köln nach London:</h2>
                      <button className='button' onClick={this.handleClick2}>Jetzt Flug buchen!</button>
                      <p>{this.state.invitation_url}</p>
                      <QRCode value={this.state.invitation_url}/>
                </div>


            </div>
    )
  }
}
export default (App)
