import React, { Component } from 'react'
import './App.css'
import axios from 'axios'
var QRCode = require('qrcode.react');

class App extends React.Component {
    constructor (probs) {
        super(probs)
        this.state = {
            invitation_url: ''
        }

        this.handleClick = this.handleClick.bind(this)
    }

    handleClick () {
        console.log('Success!')
        axios.post('http://localhost:7080/connections/create-invitation').then(response => this.setState({invitation_url: response.data.invitation_url}))
    }

  render () {
    return (
        <div className='App'>
            <div className='button__container'>
                <h2> Invitation URL:</h2>
              <button className='button' onClick={this.handleClick}>
                  Click Me
              </button>
                <p>{this.state.invitation_url}</p>
                <QRCode value={this.state.invitation_url}/>

                <h2> Create Schema: </h2>
                <br/>
                <input className="schema" type= "text" id="normal" name="1" value="" placeholder={"Create Schema here"}
                />
            </div>
        </div>
    )
  }
}
export default (App)
