import React, {Component} from 'react';
import axios from "axios";


class PostForm extends Component {
    constructor(props) {
        super(props);

        this.state = {
            attributes: ["Name,"," Alter"],
            schema_name: '',
            schema_version: '',
            new_schema: [""]
        }
        this.changeHandler = this.changeHandler.bind(this);
    }

    changeHandler = (e) => {
        this.setState({[e.target.name]: e.target.value})
    }

    submitHandler = e => {
        e.preventDefault()
        console.log(this.state)
        axios.post('http://localhost:7080/schemas',this.state).then(response => (this.setState({new_schema: response.data.new_schema})))
    }

    render() {
        const {attributes, schema_name, schema_version} = this.state
        return (
            <div>
                <form onSubmit={this.submitHandler}>
                    <div>Attribute: {attributes} </div>
                    Schema-Name:
                    <div>
                        <input type="text"
                               name="schema_name"
                               value={schema_name}
                               onChange={this.changeHandler}
                        />
                    </div>
                    Schema-Version:
                    <div>
                        <input type="text"
                               name="schema_version"
                               value={schema_version}
                               onChange={this.changeHandler}
                        />
                    </div>
                    <button type="submit">Submit</button>
                </form>
                <h2> New Schema: </h2>
                <p>{this.new_schema}</p>
            </div>
        );
    }
}


export default PostForm;
